import { useState } from 'react'
import Head from 'next/head'
const axios = require('axios')

const Index = () => {

  const [resObj, setResObj] = useState({})

  const handleClick = (e) => {
    e.preventDefault()

    axios.get('https://api.chucknorris.io/jokes/random')
      .then(res => {
        setResObj(res)
      })
      .catch(err => console.log(err))
  }

  return (
    <div className='container text-center mt-5 col-md-auto'>
      <Head>
        <link rel="stylesheet" href="https://bootswatch.com/4/materia/bootstrap.min.css" />
      </Head>
      <div class="card">
        <div class="card-body">
          <h3>Chuck Norris Fact Generator</h3>
          <button className='mt-3' onClick={handleClick}>Generate Chuck Norris Fact</button>
          {
            Object.keys(resObj).length >= 1 ? <p className='mt-3'>{resObj.data.value}</p>
              : ''
          }
        </div>
      </div>
    </div>
  )
}

export default Index
