import { useState } from 'react'
const axios = require('axios')

const Index = () => {

  const [resObj, setResObj] = useState({})

  const handleClick = (e) => {
    e.preventDefault()

    axios.get('https://api.chucknorris.io/jokes/random')
      .then(res => {
        setResObj(res)
      })
      .catch(err => console.log(err))
  }

  return (
    <div>
      <h3>Chuck Norris Fact Generator</h3>
      <button onClick={handleClick}>Generate Chuck Norris Fact</button>
      {
        Object.keys(resObj).length >= 1 ? <p>{resObj.data.value}</p>
          : ''
      }
    </div>
  )
}

export default Index
